mapper.select:
select member_no, member_pass, member_position, member_name
from show_member
where member_email = #{email}

select member_email
from show_member
where member_name = #{name} and member_phone = #{phone}

mapper.update:
update show_member
set member_pass = #{member_pass}
where member_email=#{member_email}

update show_member
set member_phone = #{member_phone}
where member_email=#{member_email}

mapper.select:
select member_no
from show_member
where member_email = #{member_email}

mapper.update:
update show_member
set member_withdraw = 'T'
where member_email=#{member_email}

mapper.select:
select member_withdraw
from show_member
where member_email = #{member_email}

select member_no
from show_member
where member_email = #{member_email} and member_pass = #{member_pass}

mapper.update:
update show_member
set member_withdraw = 'F'
where member_no=#{member_no}
